definition.script:
@NonCPS
def getBuildUser() {
    try {
        return currentBuild.rawBuild.getCause(Cause.UserIdCause).getUserId()
    }
    catch(Exception e) {
      return "UNKNOWN"
    }
}

def set_common_variables(){
  env.RELEASE_VER   = "0.1.0"
  env.CFG_REPO      = "application-deployment-charts"
  env.CFG_BRNCH     = "main"
  env.GIT_URL       = "git@github.com:shopkartDev"
  env.CHART_BUCKET  = "charts.shopkart.io"
  env.ECR_REPO      = "959986003692.dkr.ecr.ap-south-1.amazonaws.com"
  env.AWS_REGION    = "ap-south-1"
  env.BUILD_USER    = getBuildUser()
}

def set_variables(){
  app_info = [
    "sample_app" : [
      "active": false,
      "chart_name": "core-service",
    ],
    "core-service" : [
      "active": true,
      "chart_name": "core-service",
    ],
    "test-dashboard" : [
      "active": true,
      "chart_name": "shopkart-dashboard",
    ],
    "auth-service" : [
      "active": true,
      "chart_name": "auth-service",
    ],
    "default" : [
      "active": false,
      "chart_name": "default",
    ],
  ]

  env_var_map = [
    "development" : [
      "name": "development"
    ],

    "staging" : [
      "name": "staging"
    ],

    "production" : [
      "name": "production"
    ],
  ]

  /* Process the paramater passed by users */
  if (IMAGE_TAG == "") {
    currentBuild.result = "UNSTABLE"
    throw new Exception("Please provide Image Tag ")
  }
  if (HELM_CHART_VERSION == ""){
    println("Evaluation HELM_CHART_VERSION from IMAGE_TAG")
    env.HELM_CHART_VERSION = "${RELEASE_VER}-${IMAGE_TAG}"
  }
}

def get_charts(CHART_NAME, CHART_VERSION){
  sh"""
    # export AWS_REGION=${AWS_REGION}
    mkdir -p charts && cd charts
    helm repo add kart_repo s3://${CHART_BUCKET}/charts
    helm pull kart_repo/${CHART_NAME} --version ${CHART_VERSION}
    tar -xvf ${CHART_NAME}-${CHART_VERSION}.tgz
    rm ${CHART_NAME}-${CHART_VERSION}.tgz
  """
}

def deploy_app(CHART_NAME){

  chart_path="charts/${CHART_NAME}"
  values_files = ""

  if (fileExists ("${chart_path}/values.${ENVIRONMENT_TYPE}.yaml")){
      values_files += " --values values.${ENVIRONMENT_TYPE}.yaml "
  }
  if (fileExists ("${chart_path}/secrets.${ENVIRONMENT_TYPE}.yaml")){
      values_files += " --values secrets.${ENVIRONMENT_TYPE}.yaml "
  }

  RELEASE_NAME = CHART_NAME.replaceAll("_","-")

  if(ENVIRONMENT_TYPE == "production"){
    withCredentials([string(credentialsId: 'production_password', variable: 'PROD_TOKEN')]) {
      if (PROD_TOKEN != PRODUCTION_TOKEN){
        throw new Exception("Invalid Production Token , Please contact admin ")
      }
    }
  }

  sh"""
    cd ${chart_path}

    aws eks update-kubeconfig --region ${AWS_REGION} --name ${ENVIRONMENT_TYPE}
    helm secrets upgrade --install ${RELEASE_NAME}  ${values_files} --set image.tag="${IMAGE_TAG}" --set image.pullPolicy="Always"  --namespace ${ENVIRONMENT_TYPE} .
    set +x
    #helm template ${RELEASE_NAME} ${values_files}  --namespace ${ENVIRONMENT_TYPE}  .
    set -x

  """
}

def parseChoice(){

  for(app_name in APP_NAMES.split(",")) {

    active = app_info.get(app_name, app_info["default"])["active"]
    chart_name = app_info.get(app_name).get("chart_name", app_name)
    env.APP_NAME = app_name

    if(app_name && active){
      get_charts(chart_name, HELM_CHART_VERSION)
      deploy_app(chart_name)
      send_deploy_notification(app_name)
    }
    else{
      println(" Given application - ${app_name} - is not active ${active} ")
    }
  }
}

def send_deploy_notification(app_name){

  switch(currentBuild.currentResult) {
    case "SUCCESS":
      DEPLOY_STATUS = "SUCCESS âœ…"
      SLACK_COLOR = "#00E676"
      msg = "Successfully deployed application ${app_name} on `${ENVIRONMENT_TYPE}` with tag `${IMAGE_TAG}`"
      break
    default:
      DEPLOY_STATUS = "Failed ðŸŸ "
      SLACK_COLOR = "#EF6C00"
      msg = "failed to deployment application ${app_name} on `${ENVIRONMENT_TYPE}` with tag `${IMAGE_TAG}`"
  }

  slack_msg = """ *${app_name} deployment ${DEPLOY_STATUS} *
  *BUILD_USER* : `${env.BUILD_USER}`
  *BUILD_URL*  : `${BUILD_URL}`
  *MSG* : *${msg}*
  """

  SLACK_CHANNEL_NAME="#build-status"

  slackSend(channel: SLACK_CHANNEL_NAME, color: SLACK_COLOR, message: slack_msg)
}

node("cd_nodes") {

  cleanWs()
  set_common_variables()
  set_variables()

  try {
    parseChoice()
    cleanWs()
  }
  catch (e) {
    currentBuild.result = "FAILED"
    send_deploy_notification(APP_NAME)
    throw e
  }
  finally{

  }
}
